var timeInSecs;
var ticker;

function startTimer(secs) {
	timeInSecs = parseInt(secs);
	ticker = setInterval("tick()", 1000)
}

function tick() {
	var secs = timeInSecs;
	if (secs > 0) {
		timeInSecs--
	} else {
		clearInterval(ticker);
		startTimer(3 * 60)
	}
	var mins = Math.floor(secs / 60);
	secs %= 60;
	document.getElementById("mins-text").innerHTML = mins == 1 || (mins > 19 && mins % 10 == 1) ? "минуты" : (mins > 1 && mins < 5) || (secs > 19 && secs % 10 > 1 && mins % 10 < 5) ? "минуты" : "минут";
	document.getElementById("secs-text").innerHTML = secs == 1 || (secs > 19 && secs % 10 == 1) ? "секунда" : (secs > 1 && secs < 5) || (secs > 19 && secs % 10 > 1 && secs % 10 < 5) ? "секунды" : "секунд";
	document.getElementById("mins").innerHTML = mins;
	document.getElementById("secs").innerHTML = secs
}
