var i = 0,
	a = 0,
	isBackspacing = !1,
	isParagraph = !1;
var textArray = ["Hire Professional Ghostwriters|Professional Ghostwriters for Your Next Best-Selling Book. Best Budget with High Quality.", "Become a Best Selling Author!|Ebook, Memoir, Content & Blog Writing Services. Top Ghostwriting Services.", "HIRE A PROFESSIONAL GHOSTWRITER FOR YOUR NEXT STORY|Get Started Today with the Best Ghostwriters with 100% Ownership Rights.", "Hire Our Award-Winning Ghostwriters|Professional Ghostwriters for Your Next Best-Selling Book. Best Budget with High Quality.", "Experienced Book Ghostwriters|Ebook, Memoir, Content & Blog Writing Services. Top Ghostwriting Services. ", "Trusted Ghostwriters For Hire|Get Started Today with the Best Ghostwriters with 100% Ownership Rights.", ];
var speedForward = 70,
	speedWait = 1000,
	speedBetweenLines = 1000,
	speedBackspace = 50;
typeWriter("output", textArray);

function typeWriter(id, ar) {
	var element = $("#" + id),
		aString = ar[a],
		eHeader = element.children("h1"),
		eParagraph = element.children("p");
	if (!isBackspacing) {
		if (i < aString.length) {
			if (aString.charAt(i) == "|") {
				isParagraph = !0;
				eHeader.removeClass("cursor");
				eParagraph.addClass("cursor");
				i++;
				setTimeout(function () {
					typeWriter(id, ar)
				}, speedBetweenLines)
			} else {
				if (!isParagraph) {
					eHeader.text(eHeader.text() + aString.charAt(i))
				} else {
					eParagraph.text(eParagraph.text() + aString.charAt(i))
				}
				i++;
				setTimeout(function () {
					typeWriter(id, ar)
				}, speedForward)
			}
		} else if (i == aString.length) {
			isBackspacing = !0;
			setTimeout(function () {
				typeWriter(id, ar)
			}, speedWait)
		}
	} else {
		if (eHeader.text().length > 0 || eParagraph.text().length > 0) {
			if (eParagraph.text().length > 0) {
				eParagraph.text(eParagraph.text().substring(0, eParagraph.text().length - 1))
			} else if (eHeader.text().length > 0) {
				eParagraph.removeClass("cursor");
				eHeader.addClass("cursor");
				eHeader.text(eHeader.text().substring(0, eHeader.text().length - 1))
			}
			setTimeout(function () {
				typeWriter(id, ar)
			}, speedBackspace)
		} else {
			isBackspacing = !1;
			i = 0;
			isParagraph = !1;
			a = (a + 1) % ar.length;
			setTimeout(function () {
				typeWriter(id, ar)
			}, 50)
		}
	}
}
